FrameTable={}
function InitMainFrameTable()
	--	local frames={}
	local k=0
	local k2=1
	local greed=0.0045
	for i=1,12 do
		FrameTable[i]={
			SelfFrame=nil, -- Основной фрейм
			IconFrame=nil, -- Его иконка
			CdIndicatorFrame=nil, -- Фрейм перезарядки
			Number=i,
			PosX=0,
			PosY=0,
			OnCD=false,
			CurrentCDTime=0,
			Timer=nil,
			PercentAmount=0,
			OnPaused=false,
			Full=0,
			CurrentCD=0,
		}
		local data=FrameTable[i]
		k=k+1

		if k==5 then
			k=1
			k2=k2+1
		end
		data.PosX=0.637+((NextPoint+greed)*(k-1))
		data.PosY=0.113-((NextPoint+greed)*(k2-1))
	end
	return FrameTable
end